package main

import (
	"fmt"
	"strings"
)

func hasRepeatingChars(s string) bool {
	for _, v := range s {
		chars := strings.Count(s, string(v))
		if chars > 1 {
			return true
		}
	}

	return false
}

func scan(s []string, windowSize int) {
	// isolate values in the slice
	for _, v := range s {
		// for each slice, slide the window one character at a time
		for i := 0; i < len(v); i++ {
			// l:r create a 4 character window
			l, r := i, i+windowSize

			// return if the window exceeds the len of string
			if r > len(v) {
				return
			}

			// check window for repeat characters
			if !hasRepeatingChars(v[l:r]) {
				fmt.Println(r)
				break
			}
		}
	}
}

func main() {
	dev := []string{
		"mjqjpqmgbljsphdztnvjfqwrcgsmlb",    //7
		"bvwbjplbgvbhsrlpgdmjqwftvncz",      //5
		"nppdvjthqldpwncqszvftbrmjlhg",      //6
		"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", //10
		"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",  //11
	}

	prod := []string{
		"",
	}

	scan(dev, 4)
	scan(prod, 4)
	scan(dev, 14)
	scan(prod, 14)
}
